properties.script:
// Now, notify Smart Salem for wellness processing completion
var notifyEndpoint = 'http://10.194.202.2:8080/dataanalytics/ticket/completeWellnessActivity';

// Create a JSON object for the request body
var notifyRequestBody = {
    mrno: $('mrn'),
    wellnessPackageName: $('wellnessPackageName'),
    clientMobile: $('clientMobile'),
    clientEmail: $('clientEmail'),
    packageAmount: $('packageAmount'),
    campaignCode: $('campaginCode'), // Fix the typo in 'campaignCode'
    promotionCode: $('promotionCode'),
    staffName: $('staffName')
};

// Convert the JSON object to a string
var requestBodyString = JSON.stringify(notifyRequestBody);

// Create an HTTP client
var httpClient = new org.apache.commons.httpclient.HttpClient();

// Create an HTTP POST method
var postMethod = new org.apache.commons.httpclient.methods.PostMethod(notifyEndpoint);

// Set the request headers
//postMethod.setRequestHeader('Content-Type', 'application/json');
postMethod.setRequestHeader('Authorization',$('authtoken')); 
// Set the request body
postMethod.setRequestEntity(new org.apache.commons.httpclient.methods.StringRequestEntity(requestBodyString, 'application/json', 'UTF-8'));

try {
    // Execute the HTTP POST request
    var statusCode = httpClient.executeMethod(postMethod);

    // Get the response body as a string
    var responseBody = postMethod.getResponseBodyAsString();

    // Check the response status code
    if (statusCode === 200) {
        // Log a success message
        logger.info('Notification Request Successful. Response: ' + statusCode);
        logger.info('Notification Response: ' + responseBody);
    } else {
        // Handle API request failure
        logger.error('Notification Request Failed. Response: ' + statusCode);
        logger.error('Notification Response: ' + responseBody);
    }
} catch (e) {
    // Handle exceptions and log the error
    logger.error('Error while making the HTTP POST request: ' + e);
}

